***********************************************************************         
* Validate and get estimate details for approvers                     *         
***********************************************************************         
                                                                                
VAGEST   NTR1  LABEL=NO                                                         
         J     *+12                                                             
         DC    C'*VAGEST '                                                      
                                                                                
         USING EGNPASD,R2                                                       
         LA    R2,IOKEY            read for global estimate number              
         XC    EGNPAS,EGNPAS                                                    
         MVI   EGNPTYP,EGNPTYPQ                                                 
         MVI   EGNPSUB,EGNPSUBQ                                                 
         MVC   EGNPCPY,CUXCPY                                                   
         MVC   EGNPNUM,RQ_EANUM                                                 
                                                                                
         MVC   CSVKEY1,EGNPAS      save key                                     
                                                                                
         GOTOR (#IOEXEC,AIOEXEC),'IOHI+IODIR+IO2'                               
         JNE   *+2                                                              
                                                                                
         CLC   CSVKEY1(EGNPCLI-EGNPASD),EGNPAS     does record exist?           
         JE    VAGEST05                                                         
                                                                                
         MVC   ROUERRV,=AL2(AE$ESTNE)                                           
         J     VAGESTN                                                          
                                                                                
VAGEST05 CLC   EGNPSOFF,SPACES     validate limit access                        
         JNH   VAGEST10                                                         
                                                                                
         USING OFFALD,R1                                                        
         L     R1,AOFFAREA                                                      
         MVC   OFFAOFFC,EGNPSOFF                                                
         MVI   OFFAACT,OFFAVAL                                                  
         DROP  R1                                                               
         GOTO1 VOFFAL              validate office                              
         JE    VAGEST10                                                         
                                                                                
         MVC   ROUERRV,=AL2(AE$SECLK)                                           
         J     VAGESTN                                                          
                                                                                
VAGEST10 GOTOR (#IOEXEC,AIOEXEC),'IOGET+IOMST+IO2'                              
         JNE   *+2                                                              
                                                                                
         USING ESTRECD,R2                                                       
         L     R2,AIO2                                                          
         MVC   CSVKEY1,0(R2)       save main record key                         
         MVC   BYTE1,ESTKLNO                                                    
                                                                                
         MVC   X#CLIC,ESTKCLI                                                   
         MVC   X#PROC,ESTKPRO                                                   
         MVC   X#JOBC,ESTKJOB                                                   
                                                                                
         MVC   SJACCNT,SPACES                                                   
         LA    RF,SJACCNT                                                       
         LLC   RE,PCLILEN                                                       
         LR    R1,RE                                                            
         SHI   RE,1                                                             
         BASR  R3,0                                                             
         MVC   0(0,RF),ESTKCLI                                                  
         EX    RE,0(R3)                                                         
         LA    RF,1(RE,RF)                                                      
         LLC   RE,PPROLEN                                                       
         SR    RE,R1                                                            
         LLC   R1,PPROLEN                                                       
         SHI   RE,1                                                             
         BASR  R3,0                                                             
         MVC   0(0,RF),ESTKPRO                                                  
         EX    RE,0(R3)                                                         
         LA    RF,1(RE,RF)                                                      
         LA    RE,L'ESTKJOB-1                                                   
         BASR  R3,0                                                             
         MVC   0(0,RF),ESTKJOB                                                  
         EX    RE,0(R3)                                                         
                                                                                
         USING EMDELD,RF                                                        
         LA    RF,ESTRFST                                                       
         MVC   X#CLAP,EMDSCA                                                    
         MVC   X#INAP,EMDSIA                                                    
         MVC   X#CRETR,EMDAPI                                                   
                                                                                
VAGEST15 LR    R3,R2                                                            
         AHI   R3,ESTRFST-ESTRECD                                               
         XR    R0,R0                                                            
         XC    X#CRETR,X#CRETR                                                  
         USING STCELD,R3                                                        
VAGEST20 CLI   STCEL,0                                                          
         JE    VAGESTY                                                          
         CLI   STCEL,STCELQ                                                     
         JNE   VAGEST25                                                         
         CLI   STCIND,STCIEST2     New aura estimate status                     
         JNE   VAGEST22                                                         
         CLI   STCETY2,STCEAEA     Added element?                               
         JNE   VAGEST25                                                         
         J     *+12                                                             
*                                                                               
VAGEST22 CLI   STCDFR,STCEADDE     Who added estimate                           
         JNE   VAGEST25                                                         
         MVC   X#CRETR,STCPERS                                                  
         J     VAGESTY                                                          
VAGEST25 LLC   R0,STCLN                                                         
         AR    R3,R0                                                            
         J     VAGEST20                                                         
*                                                                               
VAGESTY  J     EXITY                                                            
                                                                                
VAGESTN  J     EXITN                                                            
         DROP  R2,RF                                                            
         EJECT                                                                  
