***********************************************************************         
* Get SJ account names (supports foreign)                             *         
***********************************************************************         
                                                                                
GETSJN   NTR1  LABEL=NO                                                         
         J     *+12                                                             
         DC    C'*GETSJN*'                                                      
                                                                                
         MVC   X#CLIC,ED_MCLIC                                                  
         MVC   X#PROC,ED_MPROC                                                  
         MVC   X#JOBC(L'ED_MJOBC),ED_MJOBC                                      
                                                                                
         MVC   X#CLINM,SPACES                                                   
         MVC   X#CLIPN,SPACES                                                   
         MVC   X#PRONM,SPACES                                                   
         MVC   X#PROPN,SPACES                                                   
         MVC   X#JOBNM,SPACES                                                   
         MVI   X#JOBDS,C' '                                                     
         MVC   X#SJAD1(5*L'ADRADD1),SPACES                                      
                                                                                
         USING ACTRECD,R2          READ CLIENT                                  
         LA    R2,IOKEY                                                         
         MVC   ACTKEY,SPACES                                                    
         MVC   ACTKCPY,CUXCPY                                                   
         MVC   ACTKUNT(2),PRODUL                                                
         LLC   RF,PCLILEN                                                       
         SHI   RF,1                                                             
         BASR  R1,0                                                             
         MVC   ACTKACT(0),TEMP                                                  
         EX    RF,0(R1)                                                         
                                                                                
         MVC   CSVKEY3,ACTKEY                                                   
                                                                                
         LA    R3,X#CLINM                                                       
         GOTOR GETNAM                                                           
                                                                                
         CLC   X#PROC,SPACES                                                    
         JNH   GETSJNX                                                          
                                                                                
         LA    R2,IOKEY            READ PRODUCT                                 
         MVC   ACTKEY,CSVKEY3                                                   
         LLC   RE,PCLILEN                                                       
         LLC   RF,PPROLEN                                                       
         SR    RF,RE                                                            
         LA    R3,TEMP(RE)                                                      
         LA    RE,ACTKACT(RE)                                                   
         SHI   RF,1                                                             
         BASR  R1,0                                                             
         MVC   0(0,RE),0(R3)                                                    
         EX    RF,0(R1)                                                         
         MVC   CSVKEY3,ACTKEY                                                   
                                                                                
         LA    R3,X#PRONM                                                       
         GOTOR GETNAM                                                           
                                                                                
         CLC   X#JOBC,SPACES                                                    
         JNH   GETSJNX                                                          
                                                                                
         LA    R2,IOKEY            READ JOB                                     
         MVC   ACTKEY,CSVKEY3                                                   
         LLC   RE,PPROLEN                                                       
         LLC   RF,PJOBLEN                                                       
         SR    RF,RE                                                            
         CHI   RF,L'ESTKJOB                                                     
         JNH   *+8                                                              
         LHI   RF,L'ESTKJOB                                                     
         LA    R3,TEMP(RE)                                                      
         LA    RE,ACTKACT(RE)                                                   
         SHI   RF,1                                                             
         BASR  R1,0                                                             
         MVC   0(0,RE),0(R3)                                                    
         EX    RF,0(R1)                                                         
                                                                                
         LA    R3,X#JOBNM                                                       
         GOTOR GETNAM                                                           
                                                                                
         L     R1,AIO3             job draft status                             
         MVI   X#JOBDS,NOQ                                                      
         TM    ACTRSTAT-ACTRECD(R1),ACTSDRFT                                    
         JZ    *+8                                                              
         MVI   X#JOBDS,YESQ                                                     
                                                                                
         MVI   X#JOBDL,NOQ                                                      
         TM    ACTRSTAT-ACTRECD(R1),ACTSLOCK                                    
         JZ    GETSJN02                                                         
         MVI   X#JOBDL,YESQ                                                     
                                                                                
GETSJN02 XR    RF,RF               For Aura check whether job is closed         
         ICM   RF,3,CUXPNUM                                                     
         CHI   RF,XPRODIKQ                                                      
         JNE   GETSJNX                                                          
         TM    ACTRSTAT-ACTRECD(R1),ACTSCLOS                                    
         JZ    GETSJNX                                                          
         MVI   X#JOBDL,C'C'        Set C for closed                             
                                                                                
GETSJNX  DS    0H                                                               
         J     EXIT                                                             
                                                                                
GETNAM   ST    RE,SAVERE                                                        
                                                                                
         MVC   TEMP2(L'ACTKULA),IOKEY+ACTKULA-ACTRECD                           
         GOTOR (#GETACN,AGETACN)                                                
         MVC   0(36,R3),TEMP2                                                   
         MVC   36(36,R3),TEMP2+40                                               
                                                                                
         L     R2,AIO3                                                          
         AHI   R2,ACTRFST-ACTRECD                                               
         USING ASTELD,R2                                                        
GETNAM2  CLI   ASTEL,ADRELQ                                                     
         JE    GETNAM8                                                          
         CLI   ASTEL,RSTELQ                                                     
         JE    GETNAM12                                                         
         CLI   ASTEL,0                                                          
         JE    GETNAMX                                                          
                                                                                
GETNAM4  LLC   R0,ASTLN                                                         
         AR    R2,R0                                                            
         J     GETNAM2                                                          
                                                                                
         USING ADRELD,R2                                                        
GETNAM8  MVC   X#SJAD1(5*L'ADRADD1),SPACES                                      
         LLC   R0,ADRNUM                                                        
         LTR   R0,R0                                                            
         JZ    GETNAM4                                                          
         LA    RF,ADRADD1                                                       
         LA    R1,X#SJAD1                                                       
GETNAM10 MVC   0(L'ADRADD1,R1),0(RF)                                            
         AHI   R1,L'ADRADD1                                                     
         AHI   RF,L'ADRADD1                                                     
         JCT   R0,GETNAM10                                                      
         J     GETNAM4                                                          
                                                                                
         USING RSTELD,R2                                                        
GETNAM12 CLI   RSTLN,RSTLN3Q                                                    
         JL    GETNAM4                                                          
         MVC   X#SJLKF,RSTLSTAT                                                 
         J     GETNAM4                                                          
                                                                                
GETNAMX  L     RE,SAVERE                                                        
         BR    RE                                                               
         DROP  R2                                                               
                                                                                
         EJECT                                                                  
