         J     *+12                                                             
         DC    C'*TSTACL*'                                                      
                                                                                
         LA    R1,DSVLVLS          SV and SX                                    
         CLC   0(2,R3),=C'SV'                                                   
         JE    TSTACL1                                                          
         LA    R1,DSXLVLS                                                       
         CLC   0(2,R3),=C'SX'                                                   
         JE    TSTACL1                                                          
         LA    R1,DSALVLS                                                       
         CLC   0(2,R3),=C'SA'      SA, SE and SQ                                
         JE    TSTACL1                                                          
         LA    R1,DSELVLS                                                       
         CLC   0(2,R3),=C'SE'                                                   
         JE    TSTACL1                                                          
         LA    R1,DSQLVLS                                                       
         CLC   0(2,R3),=C'SQ'                                                   
         JE    TSTACL1                                                          
*&&UK                                                                           
         LA    R1,DSILVLS                                                       
         CLC   0(2,R3),=C'SI'                                                   
         JE    TSTACL1                                                          
*&&                                                                             
         J     EXITN                                                            
                                                                                
TSTACL1  CLI   DSVLVLS,L'ACTKACT   any structure at all?                        
         JE    EXITN                                                            
                                                                                
TSTACL2  CLI   1(R1),L'ACTKACT     find length of last high level               
         JE    TSTACL3                                                          
         AHI   R1,1                                                             
         J     TSTACL2                                                          
                                                                                
TSTACL3  LHI   RE,L'ACTKACT                                                     
         LLC   RF,0(R1)                                                         
         SR    RE,RF                                                            
         SHI   RE,1                                                             
         AR    RF,R3                                                            
         AHI   RF,2                                                             
         BASR  R2,0                test account is low level                    
         EX    RE,8(R2)                                                         
         JNE   EXITN                                                            
         CLC   0(0,RF),SPACES                                                   
                                                                                
         LHI   RF,L'ACTKULA                                                     
         LA    R1,L'ACTKULA-1(R3)                                               
                                                                                
TSTACL4  CLI   0(R1),X'40'                                                      
         JH    TSTACL5                                                          
         SHI   R1,1                                                             
         JCT   RF,TSTACL4                                                       
         DC    H'0'                                                             
                                                                                
