         B     XIT                                                              
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT WORK-CODE/SUFFIX                                  *         
***********************************************************************         
         SPACE 1                                                                
FWCSUFF  NTR1  ,                                                                
         L     R2,FMTADATA                                                      
         USING TXTKWRK,R2                                                       
         MVC   FMTOUT(L'TXTKWRK),TXTKWRK                                        
         CLI   TXTKWSUF,0          IGNORE SUFFIX IF ZERO                        
         BE    XIT                                                              
         CLI   TXTKWSUF,C' '       PRINT SUFFIX AS CHARACTER                    
         BL    *+14                                                             
         MVC   FMTOUT+L'TXTKWRK(L'TXTKWSUF),TXTKWSUF                            
         B     XIT                                                              
         MVI   FMTOUT+L'TXTKWRK,C'/'  PRINT SUFFIX AS HEX-CODE                  
         GOTO1 HEXOUT,DMCB,TXTKWSUF,FMTOUT+L'TXTKWRK+1,L'TXTKWSUF,     X        
               HEXTOG                                                           
         B     XIT                                                              
         DROP  R2                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT CATEGORY FORMULA                                  *         
***********************************************************************         
         SPACE 1                                                                
FCADINST NTR1  ,                                                                
         L     RF,FMTADATA                                                      
         USING CADINST,RF                                                       
         IC    RE,CADLINP                                                       
         SH    RE,=Y(L'CADLINP+1)                                               
         MVC   FMTOUT(0),CADINP                                                 
         EX    RE,*-6                                                           
         B     XIT                                                              
         DROP  RF                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT STUDIO OFFICE/CLIENT                              *         
***********************************************************************         
         SPACE 1                                                                
FSTUOCLN NTR1  ,                                                                
         L     RF,FMTADATA                                                      
         USING STUOCLN,RF                                                       
         MVC   FMTOUT(L'STUOFF),STUOFF                                          
         MVI   FMTOUT+L'STUOFF,C'/'                                             
         MVC   FMTOUT+L'STUOFF+1(L'STUCLN),STUCLN                               
         B     XIT                                                              
         DROP  RF                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT BATCH TYPE/LOCKED MONTH/SECURITY LEVEL            *         
***********************************************************************         
         SPACE 1                                                                
FMOAMON  NTR1  ,                                                                
         MVC   LIST,BLANKS         INITIALIZE LIST                              
         LA    R1,LIST                                                          
         ST    R1,ALISPOSN         CURRENT LIST POSITION                        
         XR    R3,R3                                                            
         TM    OVRIND,OVREOL       TEST OVERRIDING END-OF-LIST                  
         BO    FMOA02                                                           
         L     R2,FMTADATA                                                      
         IC    R3,0(R2)            NUMBER OF SUB-ELEMENTS                       
         LA    R2,1(R2)                                                         
         USING MSLVALS,R2          A(SUB-ELEMENTS)                              
         MVI   BYTE,1              NEW FORMAT                                   
         CLI   MSLVTYP,0           DON'T SHOW DEFAULT                           
         BE    FMOA16                                                           
         MVI   BYTE,0              OLD FORMAT                                   
         B     FMOA04                                                           
FMOA02   L     R2,OVRADATA         SET OVERRIDE VALUES IF REQUIRED              
         IC    R3,OVRELS                                                        
         MVC   BYTE,OVRSIND                                                     
         OI    OVRIND,OVRNON                                                    
*                                                                               
FMOA04   NI    OVRIND,X'FF'-OVREOL                                              
         MVC   FMTOUT,BLANKS                                                    
         XR    R0,R0                                                            
         IC    R0,MSLVTYP          EDIT BATCH TYPE                              
         EDIT  (R0),(2,FMTOUT),0,ALIGN=LEFT                                     
         LA    R4,FMTOUT                                                        
         AR    R4,R0                                                            
         MVI   0(R4),C','                                                       
*                                  CONVERT 1-9,A-C TO JAN-DEC                   
         CLI   MSLVMON,C' '                                                     
         BNH   FMOA08                                                           
         SR    R5,R5                                                            
         IC    R5,MSLVMON                                                       
         CLI   1(R2),C'1'          IF X'C1'- X'C3'                              
         BNL   *+8                                                              
         LA    R5,9(R5)            CONVERT TO X'CA' - X'CC'                     
         SLL   R5,32-4                                                          
         SRL   R5,32-4             NOW R5=X'01' - X'0C'                         
         CVD   R5,DUB                                                           
         ICM   R0,3,DUB+L'DUB-2                                                 
         SRL   R0,4                R0=MONTH IN PWOS FORMAT                      
         MVC   WORK(1),TODAYP      TAKE CURRENT PWOS YEAR (OLD FORMAT)          
         CLI   BYTE,1                                                           
         BNE   *+10                                                             
         MVC   WORK(1),MSLVYEAR    OR USE ACTUAL YEAR (NEW FORMAT)              
         MVI   WORK+1,X'01'        JAN                                          
         MVI   WORK+2,X'01'        FIRST                                        
         CLI   BYTE,1              IF OLD FORMAT                                
         BE    FMOA06                                                           
         CLM   R0,1,TODAYP+1       AND LOCKED MONTH >= CURRENT MONTH            
         BL    FMOA06                                                           
         GOTO1 DATCON,DMCB,(X'31',WORK),(1,WORK),(4,0) BACK UP ONE YEAR         
FMOA06   GOTO1 DATCON,DMCB,(1,WORK),(X'20',WORK+3),0                            
         MVC   4(2,R4),WORK+3      DROP YEAR INTO OUTPUT STRING                 
         MH    R5,=Y(3)                                                         
         L     RF,ADO                                                           
         LA    RF,(AC@MNAMS-DO)-3(R5,RF)  INDEX INTO DD MONTH NAMES             
         MVC   1(3,R4),0(RF)                                                    
FMOA08   TM    MSLVSEC,MSLVSDEF+MSLVSSEC+MSLVSOFF  TEST SECURITY SET            
         BZ    FMOA14                                                           
         MVI   6(R4),C'/'                                                       
         LA    R4,7(R4)                                                         
         L     RF,ADO                                                           
         AH    RF,=Y(ED@SECLV-DO)                                               
         MVC   0(14,R4),0(RF)      'SECURITY LEVEL'                             
         MVI   14(R4),C'='                                                      
         LA    R4,15(R4)                                                        
         L     RF,ADO                                                           
         TM    MSLVSEC,MSLVSDEF                                                 
         BZ    FMOA10                                                           
         AH    RF,=Y(DV@BLNK-DO)                                                
         MVC   0(7,R4),0(RF)       '(BLANK)'                                    
         B     FMOA14                                                           
FMOA10   TM    MSLVSEC,MSLVSSEC+MSLVSOFF                                        
         BZ    FMOA14                                                           
         BNO   *+8                                                              
         MVI   2(R4),C','                                                       
         TM    MSLVSEC,MSLVSSEC                                                 
         BZ    FMOA12                                                           
         AH    RF,=Y(DV@LGR-DO)    'LE'                                         
         MVC   0(2,R4),0(RF)                                                    
         LA    R4,3(R4)                                                         
         L     RF,ADO                                                           
FMOA12   TM    MSLVSEC,MSLVSOFF                                                 
         BZ    FMOA14                                                           
         AH    RF,=Y(DV@OFF-DO)    'OF'                                         
         MVC   0(2,R4),0(RF)                                                    
*                                                                               
FMOA14   L     R5,ALISPOSN         MOVE BATCH DETAILS TO LIST                   
         MVC   0(L'LINDVAL,R5),FMTOUT                                           
         LA    R5,L'LINDVAL(R5)                                                 
         ST    R5,ALISPOSN                                                      
FMOA16   LA    R2,L'MSLVALS(R2)    BUMP TO NEXT SUB-ELEMENT                     
         CLI   BYTE,1                                                           
         BNE   *+8                                                              
         LA    R2,L'MSLVALS2-L'MSLVALS(R2)                                      
         LA    R0,LIST+L'LIST      TEST LIST ALMOST FULL                        
         LA    R5,L'LINDVAL(R5)                                                 
         CR    R5,R0                                                            
         BL    FMOA18                                                           
         ST    R2,OVRADATA         SAVE A(NEXT SUB-ELEMENT)                     
         BCTR  R3,0                                                             
         LTR   R3,R3                                                            
         BZ    FMOA20                                                           
         STC   R3,OVRELS           SAVE NUMBER OF SUB-ELEMENTS LEFT             
         MVC   OVRSIND,BYTE        SAVE ROUTINE INDICATORS                      
         OI    OVRIND,OVREOL       SET TO OVERRIDE END-OF-LIST                  
         B     *+8                                                              
FMOA18   BCT   R3,FMOA04                                                        
FMOA20   MVC   FMTOUT,LIST                                                      
FMOAX    B     XIT                                                              
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT CLIENT/PRODUCT PERCENTAGES                        *         
***********************************************************************         
         SPACE 1                                                                
FCPLIST  NTR1  ,                                                                
         L     R2,FMTADATA                                                      
         USING CALDATA,R2                                                       
         MVC   FMTOUT(L'CALCLPR),CALCLPR                                        
         LA    RF,FMTOUT+L'CALCLPR+1                                            
         EDIT  (P3,CALPCNT),(6,(RF)),2,ALIGN=LEFT                               
         B     XIT                                                              
         DROP  R2                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO EXPAND COST ALLOCATION PROFILE VALUES                    *         
* FORMAT: (PROFILE) = (VALUE)                                         *         
***********************************************************************         
         SPACE 1                                                                
FCAPROF  NTR1  ,                                                                
         L     R2,FMTADATA         R2=A(PROFILE ELEMENT)                        
         USING OPDELD,R2                                                        
         L     RE,ACAPTAB                                                       
         USING CAPTABD,RE                                                       
         LA    R3,FMTOUT                                                        
         L     RF,ADO                                                           
         MVC   0(EDL,R3),AC@UNKWN-DO(RF)  PRESET 'UNKNOWN'                      
FCAP02   CLI   CAPTNUM,0                                                        
         BE    FCAPX                                                            
         CLC   CAPTNUM,OPDNUM      FIND TABLE ENTRY FOR THIS PROFILE            
         BE    *+12                                                             
         LA    RE,CAPTABL(RE)                                                   
         B     FCAP02                                                           
*                                                                               
         L     R1,ADO                                                           
         AH    R1,CAPTNAM                                                       
         MVC   0(EDL,R3),0(R1)     PROFILE NAME                                 
         LA    R3,EDL(R3)                                                       
         BCTR  R3,0                                                             
         CLI   0(R3),C' '          FIND END OF NAME                             
         BNH   *-6                                                              
         MVC   1(2,R3),=C'=?'      PRESET PROFILE VALUE NOT FOUND               
         ICM   RF,15,CAPTALIT      RF=A(LIST OF VALUES)                         
         BZ    FCAP08              NONE - USE VALUE DIRECT FROM ELEMENT         
         TM    CAPTIND,CAPTIRO     TEST CAPTALIT IS A(FORMAT ROUTINE)           
         BZ    *+10                                                             
         BASR  RE,RF                                                            
         B     FCAPX                                                            
*                                  PROCESS PROFILE VALUES LIST                  
         USING CAPLSTD,RF                                                       
         SR    R0,R0                                                            
         IC    R0,CAPLNVL          R0=N'POSSIBLE VALUES FOR PROFILE             
         SR    RE,RE                                                            
         IC    RE,CAPLLXC          RE=MIN L'COMPARE FOR THESE VALUES            
         LA    RF,CAPLLIT          RF=A(FIRST LIST ENTRY)                       
FCAP04   L     R1,ADO                                                           
         AH    R1,0(RF)            R1=A(DD TEXT)                                
         EX    RE,*+8                                                           
         BE    FCAP06                                                           
         CLC   0(0,R1),OPDDATA     MATCH AGAINST ELEMENT DATA                   
         LA    RF,L'CAPLLIT(RF)    BUMP TO NEXT                                 
         BCT   R0,FCAP04                                                        
         B     FCAP08              NOT IN LIST - USE ELEMENT DATA               
*                                                                               
FCAP06   MVC   2(DVL,R3),0(R1)     MOVE IN EXPANDED PROFILE VALUE               
         B     FCAPX                                                            
*                                                                               
FCAP08   IC    RE,OPDLN            TAKE PROFILE VALUE DIRECTLY FROM EL          
         SH    RE,=Y(OPDLN1Q+1)                                                 
         MVC   2(0,R3),OPDDATA                                                  
         EX    RE,*-6                                                           
         B     FCAPX                                                            
*                                                                               
FCAPPC   LR    R4,RE                                                            
         EDIT  (P4,OPDDATA),(16,2(R3)),4,ALIGN=LEFT                             
         LR    RE,R4                                                            
         BR    RE                                                               
*                                                                               
FCAPDTS  SR    R1,R1               DEFAULT TIMESHEET OPTION                     
         IC    R1,OPDDATA                                                       
         LA    R0,C'B'                                                          
         SR    R1,R0               CONVERT 'B'-'E' TO 0 - 3                     
         SLL   R1,1                *2                                           
         LA    R1,DTSTAB(R1)                                                    
         L     RF,ADO                                                           
         AH    RF,0(R1)                                                         
         MVC   2(3,R3),0(RF)                                                    
         BR    RE                                                               
*                                                                               
DTSTAB   DC    Y(DV@NB-DO,DV@NBPL-DO,DV@BPL-DO,DV@B-DO)                         
*                                                                               
FCAPX    B     XIT                                                              
         DROP  R2,RF                                                            
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO INDEX INTO DAY-OF-WEEK NAMES                             *         
***********************************************************************         
         SPACE 1                                                                
FTMRDAY  NTR1  ,                                                                
         L     R2,FMTADATA         R2=A(TMRPEND DAY NUMBER)                     
         SR    R1,R1                                                            
         IC    R1,0(R2)                                                         
         MH    R1,=Y(3)                                                         
         L     RF,ADO                                                           
         LA    RF,(AC@DNAMS-DO)-3(R1,RF)  INDEX INTO DD DAY NAMES               
         MVC   FMTOUT(3),0(RF)                                                  
