***********************************************************************         
* ROUTINE TO FORMAT TIMESHEET PERIODS AS DD/MM-DD/MM                  *         
***********************************************************************         
         SPACE 1                                                                
FTMPPER  NTR1  ,                                                                
         L     R2,FMTADATA         R2=A(TMPEL)                                  
         USING TMPELD,R2                                                        
         LA    R3,FMTOUT                                                        
         EDIT  (1,TMPNUMB),(2,0(R3)),ZERO=NOBLANK                               
         MVI   2(R3),C','                                                       
         LA    R3,4(R3)                                                         
         GOTO1 DATCON,DMCB,(1,TMPSTART),(10,0(R3))                              
         MVI   5(R3),C'-'                                                       
         LA    R3,6(R3)                                                         
         GOTO1 DATCON,DMCB,(1,TMPEND),(10,0(R3))                                
         MVC   5(3,R3),BLANKS      CLEAR '/YY'                                  
         B     XIT                                                              
         DROP  R2                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT BUDGET ACCOUNT EXPRESSIONS AS UL=N                *         
***********************************************************************         
         SPACE 1                                                                
FACEQLV  NTR1  ,                                                                
         L     R2,FMTADATA                                                      
         USING BIVAUNT,R2                                                       
         MVC   FMTOUT(L'BIVAUNT+L'BIVALDG),BIVAUNT                              
         MVI   FMTOUT+L'BIVAUNT+L'BIVALDG,C'='                                  
         MVC   FMTOUT+L'BIVAUNT+L'BIVALDG+1(L'BIVACLV),BIVACLV                  
         OI    FMTOUT+L'BIVAUNT+L'BIVALDG+1,X'F0'                               
         B     XIT                                                              
         DROP  R2                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO FORMAT BUDGET CONTRA A/C EXPRESSIONS LIST                *         
***********************************************************************         
         SPACE 1                                                                
FCONTRA  NTR1  ,                                                                
         L     R2,FMTADATA                                                      
         USING BIVELD,R2                                                        
         LA    R3,FMTOUT                                                        
         SR    R1,R1                                                            
         IC    R1,BIVLN                                                         
         AR    R1,R2                                                            
         BCTR  R1,0                R1=END OF ELEMENT                            
         LA    R0,3                BXLE INCREMENT                               
         LA    R2,BIVVALS                                                       
         USING BIVVALS,R2                                                       
         B     *+12                                                             
*                                                                               
FCON02   MVI   0(R3),C','          FORMAT OUTPUT (GIGO)                         
         LA    R3,1(R3)                                                         
         CLI   BIVVCUNT,0                                                       
         BNE   FCON06                                                           
         L     RF,ADO                                                           
*        MVC   0(3,R3),AC@ALL-ADO(RF)                                           
         MVC   0(3,R3),AC@ALL-DO(RF)                                            
         B     FCON08                                                           
*                                                                               
FCON06   MVC   0(2,R3),BIVVCUNT    +N(N)                                        
         CLI   BIVVCUNT,C'+'                                                    
         BNE   FCON04                                                           
         OI    1(R3),X'F0'                                                      
         LA    R3,2(R3)                                                         
         CLI   BIVVCACL,0                                                       
         BE    FCON08                                                           
         MVI   0(R3),C'('                                                       
         EDIT  (1,BIVVCACL),(1,1(R3))                                           
         MVI   2(R3),C')'                                                       
         B     FCON08                                                           
*                                                                               
FCON04   LA    R3,1(R3)            U, UL, UL=N                                  
         CLI   BIVVCLDG,0                                                       
         BE    FCON08                                                           
         LA    R3,1(R3)                                                         
         CLI   BIVVCACL,0                                                       
         BE    FCON08                                                           
         MVI   0(R3),C'='                                                       
         MVC   1(1,R3),BIVVCACL                                                 
         OI    1(R3),X'F0'                                                      
         LA    R3,2(R3)                                                         
FCON08   BXLE  R2,R0,FCON02                                                     
         B     XIT                                                              
         DROP  R2                                                               
         SPACE 3                                                                
