***********************************************************************         
* ROUTINE TO FORMAT SORT SEQUENCE AND APPEND USER-ID                  *         
***********************************************************************         
         SPACE 1                                                                
FSRTSEQ  NTR1  ,                                                                
*                                                                               
         L     R4,FMTAEL           R4=A(OCNEL)                                  
         USING OCNELD,R4                                                        
         L     RF,=A(SORTAB)                                                    
FSRT10   CLI   0(RF),X'FF'                                                      
         BE    FSRT20                                                           
         CLC   OCNSORT,0(RF)                                                    
         BE    *+12                                                             
         LA    RF,L'SORTAB(RF)                                                  
         B     FSRT10                                                           
         MVC   TRWORK(L'SORTAB-1),1(RF) MOVE IN SORT DESCRIPTION                
         B     FSRT30                                                           
FSRT20   MVC   TRWORK(8),=C'(CODE)  ' DEFAULT IS '(CODE)'                       
*                                  INSERT USER-ID                               
FSRT30   BAS   RE,DISPID           DISPLAY ID                                   
*                                                                               
         L     R3,FULL             RESTORE PRINT LOCATION                       
         MVC   2(L'SORTAB-1,R3),TRWORK  REPLACE DATA ITEM                       
         B     XIT                                                              
         DROP  R4                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO GET STACK=                                               *         
***********************************************************************         
         SPACE 1                                                                
FGSTACK  NTR1  ,                                                                
*                                                                               
         L     R4,FMTAEL           R4=A(OCNEL)                                  
         USING OCNELD,R4                                                        
         LAY   R3,SCANBLK                                                       
*                                                                               
         MVC   TRWORK,SPACES                                                    
         MVC   0(20,R3),SPACES                                                  
         MVC   0(5,R3),=C'STACK'                                                
         SR    R1,R1                                                            
         ICM   R1,3,OCNSEQN        SEQUENCE                                     
         CVD   R1,DUB                                                           
         OI    DUB+7,X'0F'                                                      
         UNPK  10(5,R3),DUB+5(3)   SEQUENCE NUMBER TO UNSCAN BLOCK              
         GOTO1 UNSCAN,DMCB,(1,SCANBLK),(C'C',TRWORK)                            
*                                                                               
         BAS   RE,DISPID           DISPLAY ID                                   
*                                                                               
         L     R2,FULL             RESTORE PRINT LOCATION                       
         MVC   2(21,R2),TRWORK     REPLACE DATA ITEM                            
*                                                                               
FGSTACKX B     XIT                                                              
         DROP  R4                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO GET LASER PRINTING                                       *         
***********************************************************************         
         SPACE 1                                                                
FGETLSR  NTR1  ,                                                                
*                                                                               
         L     R4,FMTAEL                                                        
         USING OCNELD,R4                                                        
         LAY   R3,SCANBLK                                                       
         L     R1,=A(STATLASR)                                                  
         MVC   TRWORK,SPACES                                                    
*                                                                               
FGLSR10  MVC   WORK(1),1(R1)       BIT FROM TABLE                               
         CLC   WORK(1),OCNLASR     STATUS BIT                                   
         BNE   FGLSR20             NOT ON                                       
         MVC   0(20,R3),SPACES     SPACES TO BLOCK                              
         MVC   0(10,R3),2(R1)      LEFT SIDE TO BLOCK                           
         CLI   0(R1),1             ONE SIDED                                    
         BE    *+10                YES                                          
         MVC   10(10,R3),12(R1)    RIGHT SIDE TO BLOCK                          
         LA    R3,20(R3)                                                        
*                                                                               
FGLSR20  CLI   0(R1),1                                                          
         BE    *+8                                                              
         LA    R1,10(R1)                                                        
         LA    R1,12(R1)                                                        
         CLI   0(R1),X'FF'         END OF TABLE                                 
         BNE   FGLSR10                                                          
*                                                                               
         GOTO1 UNSCAN,DMCB,(1,SCANBLK),(C'C',TRWORK)                            
*                                                                               
         BAS   RE,DISPID           DISPLAY ID                                   
*                                                                               
         L     R2,FULL             RESTORE PRINT LOCATION                       
         MVC   2(21,R2),TRWORK     REPLACE DATA ITEM                            
*                                                                               
FGETLSRX B     XIT                                                              
         DROP  R4                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO TEST STATUS BYTE2 AND DISPLAY DATA                       *         
***********************************************************************         
         SPACE 1                                                                
FGSTAT2  NTR1  ,                                                                
*                                                                               
         L     R4,FMTAEL                                                        
         USING OCNELD,R4                                                        
         L     R1,=A(STATLST2)    R1=STATUS TABLE 2                             
         LA    R5,OCNSTAT2                                                      
         B     FGSTAT05                                                         
*                                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO TEST STATUS BYTE AND DISPLAY DATA                        *         
***********************************************************************         
         SPACE 1                                                                
FGSTAT   NTR1  ,                                                                
*                                                                               
         L     R4,FMTAEL                                                        
         USING OCNELD,R4                                                        
         L     R1,=A(STATLST)     R1=STATUS TABLE                               
         LA    R5,OCNSTAT                                                       
FGSTAT05 LAY   R3,SCANBLK                                                       
         MVC   TRWORK,SPACES                                                    
         SR    R0,R0               COUNTER                                      
*                                                                               
FGSTAT10 MVC   WORK(1),1(R1)       BIT FROM TABLE                               
         NC    WORK(1),0(R5)       STATUS BIT                                   
         BZ    FGSTAT30            NOT ON                                       
         CLC   2(10,R1),=CL10'MICR'                                             
         BNE   FGSTAT20                                                         
         CLI   OCNLASR,0                                                        
         BNE   FGSTAT30                                                         
*                                                                               
FGSTAT20 AHI   R0,1                COUNT OUTPUT ITEMS                           
         MVC   0(20,R3),SPACES     SPACES TO BLOCK                              
         MVC   0(10,R3),2(R1)      LEFT SIDE TO BLOCK                           
         CLI   0(R1),1             ONE SIDED                                    
         BE    *+10                YES                                          
         MVC   10(10,R3),12(R1)    RIGHT SIDE TO BLOCK                          
         LA    R3,20(R3)                                                        
*                                                                               
FGSTAT30 CLI   0(R1),1                                                          
         BE    *+8                                                              
         LA    R1,10(R1)                                                        
         LA    R1,12(R1)                                                        
         CLI   0(R1),X'FF'         END OF TABLE                                 
         BNE   FGSTAT10                                                         
*                                                                               
         GOTO1 UNSCAN,DMCB,((R0),SCANBLK),(C'C',TRWORK)                         
*                                                                               
         BAS   RE,DISPID             DISPLAY ID                                 
*                                                                               
         L     R2,FULL               RESTORE PRINT LOCATION                     
         LR    R5,R0                                                            
         MHI   R5,21                                                            
         BCTR  R5,0                                                             
         MVC   2(0,R2),TRWORK     REPLACE DATA ITEM                             
         EX    R5,*-6                                                           
*                                                                               
FGSTATX  B     XIT                                                              
         DROP  R4                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO CONVERT BUCKET TYPE                                      *         
***********************************************************************         
         SPACE 1                                                                
FBUKTYP  NTR1  ,                                                                
*                                                                               
         L     R2,FMTADATA                                                      
*                                                                               
         CLI   0(R2),C'1'          SHOW 1 OR S FOR SALARY                       
         BE    FBUK10                                                           
         CLI   0(R2),C'S'                                                       
         BE    FBUK10                                                           
         CLI   0(R2),C'2'          SHOW 2 OR P FOR PENSION                      
         BE    FBUK20                                                           
         CLI   0(R2),C'P'                                                       
         BE    FBUK20                                                           
         CLI   0(R2),C'3'          SHOW 3 OR B FOR BENEFIT                      
         BE    FBUK30                                                           
         CLI   0(R2),C'B'                                                       
         BE    FBUK30              FOR ANYTHING ELSE DON'T SHOW B/C             
         B     FBUKX               IT'S NOT VALID AND NOT HONORED               
*                                                                               
FBUK10   MVI   FMTOUT,C'S'                                                      
         B     FBUKX                                                            
FBUK20   MVI   FMTOUT,C'P'                                                      
         B     FBUKX                                                            
FBUK30   MVI   FMTOUT,C'B'                                                      
         B     FBUKX                                                            
*                                                                               
FBUKX    B     XIT                                                              
*        DROP  R2                                                               
         SPACE 3                                                                
***********************************************************************         
* ROUTINE TO CONVERT ACTION TYPE                                      *         
***********************************************************************         
         SPACE 1                                                                
FACTTYP  NTR1  ,                                                                
*                                                                               
         L     R2,FMTADATA                                                      
*                                                                               
         MVC   FMTOUT(L'GLPACTN),0(R2)                                          
         CLI   0(R2),0             IF NO ACTION THAN SHOW THE DEFAULT           
         BNE   FACTX               OF +                                         
         MVI   FMTOUT,C'+'                                                      
*                                                                               
FACTX    B     XIT                                                              
*        DROP  R2                                                               
         SPACE 3                                                                
