***********************************************************************         
* ROUTINE TO FORMAT START AND FINISH NUMBERS OF PRINT HEX LINE        *         
*                                                                     *         
* NTRY: P1=A(OUTPUT)                                                  *         
*       P2=L(OUTPUT)                                                  *         
*       P3=A(START OF RECORD)                                         *         
***********************************************************************         
STARTFIN NTR1  BASE=*,LABEL=*                                                   
         LM    R2,R4,0(R1)                                                      
         SR    R4,R2               FORMAT START NUMBER                          
         LPR   R4,R4                                                            
         MVC   LIST,BLANKS                                                      
         CURED (R4),(4,WORK),0,ALIGN=LEFT                                       
         GOTOR LISTADD,DMCB,(4,WORK),0                                          
         AR    R4,R3               FORMAT END NUMBER                            
         BCTR  R4,0                                                             
         CURED (R4),(4,WORK),0,ALIGN=LEFT                                       
         GOTOR LISTADD,DMCB,(4,WORK),0                                          
         SPACE 1                                                                
         MVC   BADLDESC,LIST                                                    
         J     XIT                                                              
                                                                                
         LTORG                                                                  
                                                                                
***********************************************************************         
* ROUTINE TO ADD TO LIST BLOCK                                        *         
*                                                                     *         
* NTRY: P1=(L(LEFT NAME), A(LEFT NAME))                               *         
*       P2=(L(RIGHT NAME),A(RIGHT NAME)) OR 0                         *         
***********************************************************************         
         SPACE 1                                                                
LISTADD  NTR1  BASE=*,LABEL=*                                                   
         SPACE 1                                                                
         CLC   LIST,BLANKS         TEST FIRST LIST ENTRY                        
         BNE   LADD2                                                            
         LA    R2,LIST             R2=A(START OF LIST)                          
         B     LADD4                                                            
         SPACE 1                                                                
LADD2    L     R2,ALISPOSN         INSERT COMMA                                 
         MVI   0(R2),C','                                                       
         LA    R2,1(R2)                                                         
         SPACE 1                                                                
LADD4    LAY   RE,SCANBLK                                                       
         CR    R2,RE                                                            
         BH    LADDX                                                            
         XR    R3,R3               COPY LEFT NAME                               
         ICM   R3,7,1(R1)                                                       
         XR    RF,RF                                                            
         IC    RF,0(R1)                                                         
         BAS   RE,LISTCOPY                                                      
         SPACE 1                                                                
         ICM   R3,7,5(R1)          TEST FOR RIGHT NAME                          
         BZ    LADDX                                                            
         MVI   0(R2),C'='          INSERT EQUALS                                
         LA    R2,1(R2)                                                         
         IC    RF,4(R1)            COPY RIGHT NAME                              
         BAS   RE,LISTCOPY                                                      
         SPACE 1                                                                
LADDX    ST    R2,ALISPOSN         SAVE A(LIST POSITION)                        
         J     XIT                                                              
         SPACE 3                                                                
***********************************************************************         
* NTRY: R2=A(OUTPUT),R3=A(INPUT),RF=L(INPUT)                          *         
***********************************************************************         
         SPACE 1                                                                
LISTCOPY BCTR  RF,0                COPY NAME                                    
         MVC   0(0,R2),0(R3)                                                    
         EX    RF,*-6                                                           
         SPACE 1                                                                
         AR    R2,RF               FIND END OF NAME                             
         CLI   0(R2),C' '                                                       
         BNE   *+10                                                             
         BCTR  R2,0                                                             
         BCT   RF,*-10                                                          
         SPACE 1                                                                
         LA    R2,1(R2)            PUT R2 AFTER NAME                            
         BR    RE                                                               
                                                                                
         LTORG                                                                  
                                                                                
         EJECT                                                                  
