         BE    GETPIDX                                                          
         CLI   SAPALEL,SAPALELQ                                                 
         BE    GETPID4                                                          
         IC    R0,SAPALLN                                                       
         BXH   R1,R0,GETPID2                                                    
         SPACE 1                                                                
GETPID4  MVC   0(L'SAPALPID,R4),SAPALPID                                        
         SPACE 1                                                                
GETPIDX  XIT1                                                                   
         DROP  R1,R2                                                            
         EJECT                                                                  
