         SR    R0,R0                                                            
         ICM   R0,1,FMT#HEAD                                                    
         BZ    DMPTAB42                                                         
         LA    R7,=CL14' Heading table'                                         
         GOTO1 PRNTBL,DMCB,(14,(R7)),FMTHEAD,(R0),                     X        
               HEADLNQ,=CL2'2H'                                                 
